import search from '../assets/search.png';
import user from '../assets/user.png';
import Cookies from 'js-cookie';
import { useNavigate, Link } from 'react-router-dom';

const NavBar = (props) => {

  const GUEST_USERID = "64bdf3eea4354c42f888ec3c"
  const userID = Cookies.get('_id').slice(3,27)
  console.log(userID)

  const handleLogout = () => {
    // Set a cookie with the name 'myCookie' and value 'Hello, Cookie!'
    Cookies.set('_id', '64bdf3eea4354c42f888ec3c');
    Cookies.set('userType', 1); 
  };

  return (
    <div className="App">
        <nav className="navbar sticky-top px-5 py-3">

          <div className="container-fluid">

            <a className="navbar-brand" href="home.html">Titikman</a>


            <div className="search-bar w-50 me-5">
                <form className="d-flex" role="search">
                  <input className="form-control me-1" type="search" placeholder="Search" aria-label="Search"/>
                  <a className="btn btn-danger" href="searched.html"><img src={search} alt=""/></a>
                </form>
            </div>


            <span className="navbar-text" id="navbarText">
                <div className="btn-group">

                  {userID === GUEST_USERID ? ( <Link to="/signup"><button type="button" className="btn btn-danger">
                  Sign In
                </button></Link> ):(
                  <>
                      <button type="button" className="btn btn-light dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                      {props.userName} <img src={user} alt="" id="user-pic"/>
                      </button>
                      <ul className="dropdown-menu">
                          <li><Link to={`/profile/${userID}`}>See Profile</Link></li>
                          <li><Link to="/"><a onClick={handleLogout} className="dropdown-item" href="index.html">Log Out</a></Link></li>
                      </ul>
                  </>
                )}

                </div>
            </span>

          </div>

      </nav>
    </div>
  );
}

export default NavBar;
