import search from '../assets/search.png';
import user from '../assets/user.png';
import Cookies from 'js-cookie';
import { useNavigate, Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';

const NavBar = (props) => {
  const GUEST_USERID = "64bdf3eea4354c42f888ec3c";
  const [userID, setUserID] = useState(Cookies.get('_id')?.slice(3, 27));
  const [loggedIn, setLoggedIn] = useState(userID !== GUEST_USERID); // State to track if the user is logged in
  const navigate = useNavigate();

  useEffect(() => {
    setUserID(Cookies.get('_id')?.slice(3, 27));
    setLoggedIn(userID !== GUEST_USERID);
  }, [userID]);

  const handleLogout = () => {
    Cookies.set('_id', GUEST_USERID);
    Cookies.set('userType', 1);
    setUserID(null);
    setLoggedIn(false); // Update the loggedIn state when the user logs out
    navigate('/');
    alert('Log Out Successful');
  };

  return (
    <div className="App">
      <nav className="navbar sticky-top px-5 py-3">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">
            Titikman
          </Link>

          <div className="search-bar w-50 me-5">
            <form className="d-flex" role="search">
              <input
                className="form-control me-1"
                type="search"
                placeholder="Search"
                aria-label="Search"
              />
              <a className="btn btn-danger" href="searched.html">
                <img src={search} alt="" />
              </a>
            </form>
          </div>

          <span className="navbar-text" id="navbarText">
            <div className="btn-group">
              {!loggedIn ? ( // Show the "Sign In" button if not logged in
                <Link to="/signup">
                  <button type="button" className="btn btn-danger">
                    Sign In
                  </button>
                </Link>
              ) : (
                // Show the user's name and profile options if logged in
                <>
                  <button
                    type="button"
                    className="btn btn-light dropdown-toggle"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    {props.userName} <img src={user} alt="" id="user-pic" />
                  </button>
                  <ul className="dropdown-menu">
                    <li>
                      <Link to={`/profile/${userID}`}>See Profile</Link>
                    </li>
                    <li>
                      <button onClick={handleLogout} className="dropdown-item">
                        Log Out
                      </button>
                    </li>
                  </ul>
                </>
              )}
            </div>
          </span>
        </div>
      </nav>
    </div>
  );
};

export default NavBar;
