import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';


const UserProfile = () => {
  const [profileData, setProfileData] = useState({});
  const [reviewData, setReviewData] = useState([]);
  const { id } = useParams();

  const fetchUserProfile = async () => {
    try {
      const response =  await axios.get(`http://localhost:4000/api/profile/${id}`); // Replace with your backend API endpoint
      console.log(response)
      if (response.ok) {
        console.log(response)
        const data = await response.json();
        setProfileData(data);
        console.log(data)
      }
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const fetchUserReviews = async () => {
    try {
      const response = await axios.get(`http://localhost:4000/api/reviewsbyuser/${id}`);  // Replace with your backend API endpoint
      if (response.ok) {
        const data = await response.json();
        setReviewData(data);
        console.log(data)
      }
    } catch (error) {
      console.error('Error fetching user reviews:', error);
    }
  };

  useEffect(() => {
    fetchUserProfile();
    fetchUserReviews();
  }, []);

  // Handle edit profile submission
  const handleEditProfile = async (formData) => {
    try {
      const response = await fetch(`/api/profile/${profileData._id}`, {
        method: 'PATCH',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        setProfileData(data);
        console.log(data)
      }
    } catch (error) {
      console.error('Error updating user profile:', error);
    }
  };

  // Handle review submission
  const handleCreateReview = async (formData) => {
    try {
      const response = await fetch(`/api/reviewnew/${profileData._id}`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        setReviewData(data);
      }
    } catch (error) {
      console.error('Error creating review:', error);
    }
  };

  return (
    <div className="container">
      <div className="row d-flex">
        <div className="col-lg-4">
          <div className="container-fluid pt-2">
            <div className="row">
              <div className="col-sm-6">
                <div className="profile-container">
                  <div className="profile-icon">
                    <img src={profileData.firstName}alt="Profile Picture" />
                  </div>
                  <h3 className="profile-name">
                    {profileData.firstName} {profileData.lastName}
                  </h3>
                  {/* ... */}
                  <button
                    type="button"
                    className="btn btn-danger edit-profile-btn"
                    data-bs-toggle="modal"
                    data-bs-target="#editprofile"
                  >
                    Edit Profile
                  </button>

                  <div className="modal fade" id="editprofile" tabIndex="-1" aria-labelledby="profileModalLabel" aria-hidden="true">
                    <div className="modal-dialog">
                      {/* ... */}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="col-lg-8 pt-2">
          <div className="cards-container px-lg-4">
            <div className="row">
              {/* Display user reviews */}
              {reviewData.map((review) => (
                <div key={review._id} className="col-lg-4 col-md-4 mb-4">
                  <div className="card">
                    <a href={`/${review._id}`}>
                      <img src={review.filename} className="card-img-top" alt={`Image ${review._id}`} />
                    </a>
                    <div className="card-body">
                      <h5 className="card-title">{review.title}</h5>
                      <p className="card-text">{review.revContent}</p>
                    </div>
                  </div>
                </div>
              ))}

              {/* Create Review Form */}
              <div className="col-lg-4 col-md-4 mb-4">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Write a Review</h5>
                    <form onSubmit={(e) => e.preventDefault()}>
                      {/* ... */}
                      {/* Use the handleCreateReview function to handle review submission */}
                      {/* ... */}
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
