import React, { useState } from 'react';
import axios from "axios"


const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  async function submit(e){
    e.preventDefault();

    try{
      const response = await axios.post("http://localhost:4000/login",{
        username, password
      });

      document.cookie = 'userType=${response.data.userType}';
      document.cookie = '_id=${response.data._id}';
    }
    catch(error){
      if (axios.isAxiosError(error)) {
        // AxiosError, handle the error here
        if (error.response) {
          // The request was made, but the server responded with an error status code
          console.error('Server responded with an error:', error.response.data);
        } else if (error.request) {
          // The request was made but no response was received
          console.error('No response received from the server:', error.request);
        } else {
          // Something else happened while setting up the request
          console.error('Error setting up the request:', error.message);
        }
      } else {
        // Some other error occurred, not related to Axios
        console.error('Error:', error.message);
      }
    }
  }

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Perform any necessary login logic here using the userType, username, and password state variables.
    console.log('Username:', username);
    console.log('Password:', password);

    try {
      const response = await fetch("/api/login", {
        method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username,
        password,
      }),
    });
  
      // Handle cookies from the response
      const cookies = response.headers.get('set-cookie');
      console.log('Cookies:', cookies);
  
      // Redirect the user or perform other actions after successful login
      // For example, redirect to the dashboard page
      // window.location.href = '/dashboard';
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div className="container justify-content-center align-items-center mt-5 pt-4">
      <div className="row">
        <div className="col-12 col-sm-8 col-md-6 m-auto">
          <div className="card mt-5">
            <div className="card-body">
              <h1 className="text-center mt-3 text-danger">Login</h1>
              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  id="username"
                  className="form-control my-4 py-2"
                  placeholder="Username"
                  value={username}
                  onChange={handleUsernameChange}
                />
                <input
                  type="password"
                  id="password"
                  className="form-control my-4 py-2"
                  placeholder="Password"
                  value={password}
                  onChange={handlePasswordChange}
                />
                <div className="text-center mt-3 mb-2">
                  <button type="submit" className="btn btn-danger">
                    Login
                  </button>
                </div>
                <small className="d-flex justify-content-center">
                  New to Titikman? &nbsp;<a href="signup.html">Sign up</a>
                </small>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
