import React, { useState } from 'react';

// User component
const User = () => {
  return (
    <div className="col-lg-4">
      <div className="container">
        <div className="row">
          <div className="col-sm-6">
            <div className="profile-container">
              <div className="profile-icon">
                <img src="assets/jerrick.jpg" alt="Profile Picture" />
              </div>
              <h3 className="profile-name">Jerrie Santi</h3>
              <br />
              <h4>Just here to review some food places I've tried hehe!</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Review component
const Review = ({ imgSrc, title, content, expandedContent }) => {
  const [isExpanded, setExpanded] = useState(false);

  const handleExpand = () => {
    setExpanded(!isExpanded);
  };

  return (
    <div className="col-lg-4 col-md-4 mb-4">
      <div className="card">
        <img src={imgSrc} className="card-img-top" alt="Image" />
        <div className="card-body">
          <h5 className="card-title">{title}</h5>
          <p className="card-text">
            <p className="text-content">
              {isExpanded ? expandedContent : content}
            </p>
          </p>
          <button onClick={handleExpand} className="expand-link">
            {isExpanded ? 'Read Less' : 'Read More'}
          </button>
        </div>
      </div>
    </div>
  );
};

// Main component
const App = () => {
  return (
    <div className="row">
      <User />
      <div className="col-lg-8">
        <div className="cards-container">
          <div className="row">
            <Review
              imgSrc="assets/jollibee.jpg"
              title="Jollibee"
              content="The chicken was good! Although the service was pretty slow when I visited,"
              expandedContent="the staff made sure everything is provided and thatâ€™s a plus!"
            />
            {/* Add other reviews here */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
