import React from 'react';

// User component
const User = () => {
  return (
    <div className="col-lg-4">
      <div className="container">
        <div className="row">
          <div className="col-sm-6">
            <div className="profile-container">
              <div className="profile-icon">
                <img src="assets/jerrick.jpg" alt="Profile Picture" />
              </div>
              <h3 className="profile-name">Jerrie Santi</h3>
              <br />
              <h4>Just here to review some food places I've tried hehe!</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Review component
const Review = ({ imgSrc, title, content }) => {
  const [isExpanded, setExpanded] = React.useState(false);

  const handleExpand = () => {
    setExpanded(!isExpanded);
  };

  return (
    <div className="col-lg-4 col-md-4 mb-4">
      <div className="card">
        <img src={imgSrc} className="card-img-top" alt="Image" />
        <div className="card-body">
          <h5 className="card-title">{title}</h5>
          <p className="card-text">
            <p className="text-content">
              {content}
              {isExpanded && (
                <span className="hidden-content hide">
                  {expandedContent}
                </span>
              )}
            </p>
          </p>
          <button onClick={handleExpand} className="expand-link">
            {isExpanded ? 'Read Less' : 'Read More'}
          </button>
        </div>
      </div>
    </div>
  );
};

// Main component
const Main = () => {
  return (
    <div className="row">
      <User />
      <div className="col-lg-8">
        <div className="cards-container">
          <div className="row">
            <Review
              imgSrc="assets/jollibee.jpg"
              title="Jollibee"
              content="The chicken was good! Although the service was pretty slow when I visited,"
              expandedContent="the staff made sure everything is provided and that’s a plus!"
            />
            <Review
              imgSrc="assets/manginasal.jpg"
              title="Mang Inasal"
              content="Inihaw! That’s what this place is famous for. I like how it has an option"
              expandedContent="to get unli rice hehe sulit!"
            />
            <Review
              imgSrc="assets/24chicken.jpg"
              title="24 Chicken"
              content="At this point, I’d say I’m a chicken connoisseur already. They have different"
              expandedContent="flavors and that’s what I appreciate more about the store."
            />
            <Review
              imgSrc="assets/starbucks.jpg"
              title="Starbucks"
              content="Coffee, sweets, and pastries. That’s what I love in this place. The baristas"
              expandedContent="are also nice!"
            />
            <Review
              imgSrc="assets/subway.jpg"
              title="Subway"
              content="Whenever I crave for sandwiches, this is where I usually go because of their"
              expandedContent="bread options! I like mine whole wheat <3"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
