import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';

function EditUser(props) {
  const [show, setShow] = useState(false);
  const [bio, setBio] = useState(props.bio);
  const [file, setFile] = useState(null);

  const userId = props.userId
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('bio', bio);
      formData.append('file', file);

      // Send the updated data to the backend
      const response = await axios.patch(`http://localhost:4000/api/profile/${userId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Handle the response (you can add further logic if needed)
      console.log('Profile updated:', response.data);

      // Close the modal after successful submission
      handleClose();
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <>
      <button
        type="button"
        className="btn btn-danger edit-profile-btn"
        onClick={handleShow}
      >
        Edit Profile
      </button>

      <Modal show={show} onHide={handleClose}>
        <form className="editmodal" onSubmit={handleSubmit}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            Update User Info
            <div className="mb-3">
              <label htmlFor="username" className="form-label">
                Bio
              </label>
              <textarea
                type="textarea"
                value={bio}
                onChange={(e) => {
                  setBio(e.target.value);
                }}
                className="form-control"
              />
              <label htmlFor="media" className="form-label">
                Upload Media:
              </label>
              <input
                type="file"
                id="media"
                name="media"
                accept="image/*, video/*"
                className="form-control"
                onChange={(e) => {
                  setFile(e.target.files[0]);
                }}
              />
            </div>
          </Modal.Body>
          <Modal.Footer>
            <button onClick={handleClose} className="btn btn-light">
              Close
            </button>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </Modal.Footer>
        </form>
      </Modal>
    </>
  );
}


export default EditUser;
